# Cell
def get_nltk_docs(df, src_fns, trgt_fns):
    src_docs, trgt_docs = [], []

    for src, trgt in zip(src_fns, trgt_fns):
        src_docs.append(df.loc[df.file == src].text.values[0])
        trgt_docs.append(df.loc[df.file == trgt].text.values[0])

    return src_docs, trgt_docs